eneral_ci NULL DEFAULT NULL COMMENT '经营项目',  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '姓名',  `number` varchar(18) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '身份证号码',  `sex` int(1) NULL DEFAULT NULL COMMENT '性别0未知1男2女',  `birth` date NULL DEFAULT NULL COMMENT '出生日期',  `marriage` int(1) NULL DEFAULT NULL COMMENT '婚姻状况1未婚2已婚3离异4丧偶',  `phone` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '联系电话',  `spouse` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '配偶姓名',  `phone2` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '配偶电话',  `remark` varchar(999) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',  PRIMARY KEY (`id`) USING BTREE,  INDEX `loan_department`(`department_id`) USING BTREE,  INDEX `loan_bank`(`bank_id`) USING BTREE,  INDEX `loan_type`(`type_id`) USING BTREE,  CONSTRAINT `loan_bank` FOREIGN KEY (`bank_id`) REFERENCES `bank` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,  CONSTRAINT `loan_department` FOREIGN KEY (`department_id`) REFERENCES `department` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,  CONSTRAINT `loan_type` FOREIGN KEY (`type_id`) REFERENCES `type` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;SET FOREIGN_KEY_CHECKS = 1;